,\"includeSubcategories\":false,\"typeScale\":5} /--></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:query-no-results -->\n<!-- wp:group {\"metadata\":{\"name\":\"Contents\",\"categories\":[\"404-template\"],\"patternName\":\"a8c/404-template-1\"},\"align\":\"wide\",\"className\":\"pointer-events-none\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|80\",\"bottom\":\"var:preset|spacing|80\"}},\"dimensions\":{\"minHeight\":\"60vw\"}},\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\"}} -->\n<div class=\"wp-block-group alignwide pointer-events-none\" style=\"min-height:60vw;padding-top:var(--wp--preset--spacing--80);padding-bottom:var(--wp--preset--spacing--80)\"><!-- wp:heading {\"textAlign\":\"center\",\"fontSize\":\"xxx-large\"} -->\n<h2 class=\"wp-block-heading has-text-align-center has-xxx-large-font-size\"><strong>No blog yet</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n<!-- /wp:query-no-results -->\n\n<!-- wp:query-pagination {\"paginationArrow\":\"chevron\",\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\"}} -->\n<!-- wp:query-pagination-previous /-->\n\n<!-- wp:query-pagination-numbers {\"midSize\":1} /-->\n\n<!-- wp:query-pagination-next /-->\n<!-- /wp:query-pagination --></div>\n<!-- /wp:query --></div>\n<!-- /wp:group -->\n\n<!-- wp:block {\"ref\":1266} /-->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->', 'Tips Terbaru 2026 untuk Software Bisnis', '', 'publish', 'closed', 'closed', '', 'artikel-id', '', '', '2025-12-05 13:17:02', '2025-12-05 06:17:02', '', 0, 'https://ozeva.wordpress.com/?page_id=1663', 0, 'page', '', 0),
